// @name         ChatGPT Chat ‚Üí JSON saver
// @namespace    nobody.chat.capture
// @version      0.3
// @description  Observe ChatGPT UI, extract messages, save/download as JSON
// @match        https://chat.openai.com/*
// @match        https://chatgpt.com/*
// @grant        none
// ==/UserScript==
(function () {
  'use strict';
  // --- UI button ---
  const btn = document.createElement('button');
  Object.assign(btn.style, {
    position: 'fixed', right: '12px', bottom: '12px',
    padding: '8px 10px', border: '1px solid #888', borderRadius: '8px',
    font: '12px/1.2 system-ui, sans-serif', background: '#111', color: '#eee',
    zIndex: 2147483647, cursor: 'pointer', opacity: '0.8'
  });
  btn.textContent = 'Save Chat JSON';
  btn.title = 'Download current chat as JSON';
  btn.addEventListener('mouseenter', () => btn.style.opacity = '1');
  btn.addEventListener('mouseleave', () => btn.style.opacity = '0.8');
  document.documentElement.appendChild(btn);
  btn.onclick = () => {
    const data = extract();
    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
    const a = document.createElement('a');
    const ts = new Date().toISOString().replace(/[:.]/g, '-');
    a.href = URL.createObjectURL(blob);
    a.download = chatgpt-${ts}.json;
    document.body.appendChild(a);
    a.click();
    a.remove();
    URL.revokeObjectURL(a.href);
  };
  // --- Mutation observer (keeps extraction live/robust) ---
  const mo = new MutationObserver(debounce(() => {
    // Optionally stash the latest in localStorage for autosave
    try { localStorage.setItem('chatgpt_capture_latest', JSON.stringify(extract())); } catch {}
  }, 500));
  mo.observe(document.body, { subtree: true, childList: true });
  // --- Core extractor ---
  function extract() {
    // Heuristics: try multiple known layouts/selectors; fall back to articles
    const containers = unique([
      ...document.querySelectorAll('[data-message-author-role]'),
      ...document.querySelectorAll('article'),
      ...document.querySelectorAll('[data-message-id]'),
    ]);
    const messages = containers
      .map(node => normalizeMessage(node))
      .filter(Boolean);
    const title = document.title;
    const url = location.href;
    return {
      title,
      url,
      captured_at: new Date().toISOString(),
      messages
    };
  }
  function normalizeMessage(node) {
    // Try to determine role
    let role =
      node.getAttribute?.('data-message-author-role') ||
      node.getAttribute?.('data-testid')?.includes('user') ? 'user' : null;
    if (!role) {
      // Try to infer from aria/screenreader labels that sometimes show ‚ÄúYou said:‚Äù / ‚ÄúChatGPT said:‚Äù
      const text = node.innerText || '';
      if (/^\s*you said/i.test(text)) role = 'user';
      else if (/^\s*chatgpt said/i.test(text)) role = 'assistant';
    }
    // Fallback by structure: look for ‚ÄúCopy‚Äù buttons or code blocks often present in assistant replies
    if (!role) {
      const hasCode = node.querySelector?.('pre, code');
      const possibleReplyRegion = node.querySelector?.('[data-message-author-role="assistant"]');
      if (possibleReplyRegion) role = 'assistant';
      else if (hasCode) role = 'assistant';
    }
    // Last resort: position order (user/assistant alternating). You can refine if needed.
    if (!role) role = guessRoleByOrder(node);
    // Extract text and HTML (innerHTML can be heavy; okay for archival)
    const html = safeHTML(node);
    const text = (node.innerText || '').trim();
    // Timestamp: occasionally present; otherwise leave null
    const timeAttr = node.querySelector?.('time')?.getAttribute?.('datetime');
    const ts = timeAttr || null;
    // A loose, stable-ish id
    const id =
      node.getAttribute?.('data-message-id') ||
      node.id ||
      String(hash(text)).slice(0, 10);
    // Filter out empties
    if (!text && !html) return null;
    return { id, role, text, html, ts };
  }
  function unique(arr) {
    const s = new Set();
    return arr.filter(x => (x && !s.has(x)) ? (s.add(x), true) : false);
  }
  function safeHTML(node) {
    // Clone to avoid live mutations; strip script tags as a precaution
    const clone = node.cloneNode(true);
    clone.querySelectorAll?.('script').forEach(s => s.remove());
    return clone.innerHTML?.trim() || '';
  }
  // Very rough role guesser if nothing else works: alternate by DOM order
  function guessRoleByOrder(node) {
    try {
      const all = [...document.querySelectorAll('[data-message-author-role], article, [data-message-id]')];
      const idx = all.indexOf(node);
      return (idx % 2 === 0) ? 'user' : 'assistant';
    } catch { return 'assistant'; }
  }
  // Tiny hash for ids
  function hash(s) {
    let h = 0, i = 0;
    while (i < s.length) h = (h * 31 + s.charCodeAt(i++)) | 0;
    return h >>> 0;
  }
  // Debounce util
  function debounce(fn, ms) {
    let t;
    return (...args) => { clearTimeout(t); t = setTimeout(() => fn(...args), ms); };
  }
