    }
    if (!role) {
      const hasCode = node.querySelector?.('pre, code');
      if (hasCode) role = 'assistant';
    }
    if (!role) role = 'assistant'; // fallback
    const text = (node.innerText || '').trim();
    if (!text) return null;
    return { role, content: text };
  }).filter(Boolean);
  return messages;
}
