(() => {
  'use strict';
  // --- thread id + HWM storage ---
  const threadId = (() => {
    const m = location.pathname.match(/\/c\/([a-f0-9-]{10,})/i);
    return (m && m[1]) || location.href.replace(/[#?].*$/, '');
  })();
  const HWM_KEY = `chat_apiarray_hwm:${threadId}`;
