  const getSeen = () => {
    try { return new Set(JSON.parse(localStorage.getItem(HWM_KEY) || '[]')); }
    catch { return new Set(); }
  };
  const setSeen = (s) => {
    try { localStorage.setItem(HWM_KEY, JSON.stringify([...s])); } catch {}
  };
  // --- extraction (filtered) ---
  function extractAll() {
    // Prefer explicit message nodes
    const nodes = [
