    if (!nodes.length) {
      nodes.push(...document.querySelectorAll('[data-message-id], article'));
    }
    const messages = [];
    for (const n of nodes) {
      // Clone so we can strip UI chrome without touching the live DOM
      const c = n.cloneNode(true);
      // Remove common CTA/UI chrome (buttons/toolbars/icons/etc.)
      c.querySelectorAll([
        'button',
        '[role="button"]',
        'svg',
        '[data-testid*="copy"]',
        '[data-testid*="turn-action"]',
        '[data-testid="copy-turn-action-button"]',
        '[data-state]', // dropdowns/menus
