      // Strip SR-only ‚ÄúYou said:‚Äù/‚ÄúChatGPT said:‚Äù headers, if present
      c.querySelectorAll('h5, h6, [class*="sr-only"], [class*="sr_only"], [class*="screenreader"]')
        .forEach(el => el.remove());
      // Keep code text but drop any ornament
      // (already removed copy widgets above)
      // Nothing extra needed; <pre>/<code> text remains.
      // Now get clean text
      let text = (c.innerText || '').replace(/\s+\n/g, '\n').trim();
      // Remove leading labels that sometimes leak into text
      text = text.replace(/^\s*(You said:|ChatGPT said:)\s*/i, '');
      if (!text) continue;
      // role
      let role = n.getAttribute('data-message-author-role');
      if (!role) {
        const t = (n.innerText || '').toLowerCase();
