      const id = idAttr || hash(text).toString(36);
      messages.push({ id, role, content: text });
    }
    // Dedup by id (last wins)
    const uniq = new Map();
    for (const m of messages) uniq.set(m.id, m);
    return [...uniq.values()];
  }
