  async function copyNew() {
    const all = extractAll();
    const seen = getSeen();
    const newOnes = all.filter(m => !seen.has(m.id));
    const apiArray = newOnes.map(({ role, content }) => ({ role, content }));
    await copyJSON(apiArray);
