    toast(`Copied NEW: ${apiArray.length} (seen: ${seen.size})`);
  }
  async function copyAll() {
    const all = extractAll();
    const apiArray = all.map(({ role, content }) => ({ role, content }));
    await copyJSON(apiArray);
    toast(`Copied ALL: ${apiArray.length}`);
  }
  function resetHWM() {
    setSeen(new Set());
    toast('High-water mark reset.');
  }
  // --- tiny UI helpers ---
  function toast(msg) {
    const el = document.createElement('div');
    el.textContent = msg;
    el.style.cssText = `
      position:fixed;left:50%;top:10%;transform:translateX(-50%);
      background:#16a34a;color:#fff;padding:6px 10px;border-radius:8px;
      font:12px/1.2 system-ui,sans-serif;z-index:2147483647;
      box-shadow:0 6px 30px rgba(0,0,0,.4)
    `;
    document.body.appendChild(el);
    setTimeout(()=>el.remove(),1500);
  }
  function quickMenu(x,y,items){
    const menu=document.createElement('div');
    menu.style.cssText=`
      position:fixed;left:${x}px;top:${y}px;
      background:#1f2937;color:#e5e7eb;border:1px solid #374151;border-radius:8px;
      font:12px/1.2 system-ui,sans-serif;z-index:2147483647;overflow:hidden;
      box-shadow:0 10px 30px rgba(0,0,0,.5)
    `;
