    const off=(ev)=>{ if(!menu.contains(ev.target)){ menu.remove(); document.removeEventListener('mousedown',off,true);} };
    document.addEventListener('mousedown',off,true);
  }
  // tiny hash for ids when no data-message-id
  function hash(s){ let h=0; for(let i=0;i<s.length;i++) h=(h*31 + s.charCodeAt(i))|0; return h>>>0; }
  // (Optional) observe DOM so future features could react; no work now
  const mo = new MutationObserver(()=>{});
  mo.observe(document.body,{childList:true,subtree:true});
